function saveLanguage(language) {
	// the user wishs to switch to the french version
	if(language === 'en'){
		url = "/en"
		data = {lang:"en"}
	}
	
	else {
		url = "/"
		data = {lang:"fr"}
	}
	$.ajax({
        url: url,
        type:"POST",
        data:data,
        success:function(data){
        	window.location.href=url;
        },
		error: function(data){
			window.location.href=url;
        }
    });
}
