  Drupal.behaviors.audioPlayer = {
    attach: function (context, settings) {
      function hameauAudioPlayer(element) {
        function stopAll() {
          element.each(function () {
            $audio = $(this).find("audio");
            $audio[0].pause();
            $(this).find(".voyage-player__pause").hide();
            $(this).find(".voyage-player__play").show();
          });
        }

        element.each(function () {
          var $play = $(this).find(".voyage-player__play"),
            $pause = $(this).find(".voyage-player__pause"),
            $progress = $(this).find(".voyage-player__progress span"),
            $time = $(this).find(".voyage-player__time span"),
            playing = false,
            $audio = $(this).find("audio"),
            $next = $(this).find(".voyage-player__next"),
            $prev = $(this).find(".voyage-player__prev"),
            $info = $(this).find(".voyage-player__info"),
            current = 0;
          var datafiles = $(this).attr("data-files");
          var tracks = datafiles.substring(0, datafiles.length - 1).split(";"),
            tracksAmount = tracks.length;

          if (tracksAmount < 2) {
            $next.removeClass("active");
          }

          $info.text("1/" + tracksAmount);
          $audio[0].src = tracks[current];

          $audio[0].loop = false;

          $play.on("click", function (e) {
            e.preventDefault();
            stopAll();
            $audio[0].play();
            $play.hide();
            $pause.show();
          });

          $pause.on("click", function (e) {
            e.preventDefault();
            $audio[0].pause();
            $pause.hide();
            $play.show();
          });

          $next.on("click", function (e) {
            e.preventDefault();
            if ($next.hasClass("active")) {
              $audio[0].pause();
              current++;
              if (current == tracksAmount - 1) {
                $next.removeClass("active");
              }
              if (current > 0) {
                $prev.addClass("active");
              }
              $audio[0].src = tracks[current];
              $info.text(current + 1 + "/" + tracksAmount);
              stopAll();
              $audio[0].play();
              $play.hide();
              $pause.show();
            } else {
              return false;
            }
          });

          $prev.on("click", function (e) {
            e.preventDefault();
            if ($prev.hasClass("active")) {
              $audio[0].pause();
              current--;
              if (current == 0) {
                $prev.removeClass("active");
              }
              if (current < tracksAmount - 1) {
                $next.addClass("active");
              }
              $audio[0].src = tracks[current];
              $info.text(current + 1 + "/" + tracksAmount);
              stopAll();
              $audio[0].play();
              $play.hide();
              $pause.show();
            } else {
              return false;
            }
          });

          $audio[0].onloadedmetadata = function () {
            var s = parseInt($audio[0].duration % 60);
            var m = parseInt($audio[0].duration / 60) % 60;
            if (s < 10) {
              s = "0" + s;
            }
            $time.html(m + ":" + s);
          };

          $audio.on("timeupdate", function (e) {
            var value = 0;
            if ($audio[0].currentTime > 0) {
              value = Math.floor(
                (100 / $audio[0].duration) * $audio[0].currentTime
              );
            }
            $progress.css("width", value + "%");
          });

          $(element).find(".voyage-player__progress").on("click", seek);

          function seek(e) {
            var percent = e.offsetX / this.offsetWidth;
            $audio[0].currentTime = percent * $audio[0].duration;
          }
        });
      }

      hameauAudioPlayer(
        $(".node-long-read-lieu.longread-hameau .voyage-player")
      );
      hameauAudioPlayer(
        $(".node-long-read-lieu.longread-maison-reine .voyage-player")
      );
    },
  };

