  Drupal.behaviors.showcase = {
    attach: function (context, settings) {
      if (
        $(".ui-showcase")
        .not(".view-display-id-node")
        .not(".view-id-widget_collections").length > 0
      ) {
        $(".ui-showcase")
          .not(".view-display-id-node")
          .not(".view-id-widget_collections")
          .each(function () {
            var $wrapper = $(this).find(".collections-wrapper"),
              $grid = $wrapper.find(".ui-grid");
            ($footer = $wrapper.find(".view-footer")),
            (item = 0),
            ($popup = $wrapper.find(".carousel-popup").empty()),
            ($items = $wrapper
              .find(".ui-grid > .item")
              .clone()
              .attr("style", "")),
            ($closeButton = $('<button class="close"></button>')),
            ($slick = $('<div class="slick-carousel theme-showcase"></div>'));
            $slick.append($items);
            $slick.imagesLoaded().always(function (instance) {
              var slickSlider = $slick.slick({
                slidesToShow: 1,
                //centerMode: true,
                //variableWidth: true,
                fade: true,
                arrows: true,
                adaptiveHeight: false,
                prevArrow: '<button type="button" data-role="none" class="slick-prev" aria-label="previous"><i class="icon-arrow-long"></i></button>',
                nextArrow: '<button type="button" data-role="none" class="slick-next" aria-label="next"><i class="icon-arrow-long"></i></button>',
                initialSlide: item,
                responsive: [{
                  breakpoint: 1000,
                  settings: {
                    fade: false,
                  },
                }, ],
              });
              slickSlider.on(
                "beforeChange",
                function (event, slick, currentSlide, nextSlide) {
                  if ($(window).width() > 1000) {
                    $(slick.$slides[currentSlide])
                      .find(".image")
                      .velocity("stop")
                      .velocity({
                          opacity: 0,
                        },
                        600
                      );
                    $(slick.$slides[currentSlide])
                      .find("figcaption")
                      .velocity("stop")
                      .velocity({
                        opacity: 0,
                        translateZ: 0, // Force HA by animating a 3D property
                        translateY: ["20px", 0],
                      }, {
                        duration: 500,
                        delay: 400,
                      });
                  }
                }
              );
              slickSlider.on(
                "afterChange",
                function (event, slick, currentSlide) {
                  if ($(window).width() > 1000) {
                    $(slick.$slides[currentSlide])
                      .find(".image")
                      .velocity("stop")
                      .velocity({
                          opacity: 1,
                        },
                        600
                      );
                    $(slick.$slides[currentSlide])
                      .find("figcaption")
                      .velocity("stop")
                      .velocity({
                        opacity: 1,
                        translateZ: 0, // Force HA by animating a 3D property
                        translateY: [0, "20px"],
                      }, {
                        duration: 500,
                        delay: 400,
                      });
                  }
                }
              );
              var msnry = $grid.data("masonry");
              var $items;
              // access Masonry properties
              $wrapper.on("click", ".ui-grid > .item", function () {
                $items = $grid.masonry("getItemElements");

                $.each($items, function (index) {
                  var $this = $(this);
                  setTimeout(function () {
                    $this.children().addClass("fadeOutUp");
                  }, 100 * index);
                });
                $footer.velocity("stop").velocity({
                  opacity: 0,
                });
                $popup.append($closeButton);
                var item = $(this).index() - 1;
                slickSlider.slick("slickGoTo", item);
                setTimeout(function () {
                  $popup.velocity("stop").velocity({
                    opacity: 1,
                  }, {
                    begin: function () {
                      $popup.css("z-index", 5);
                    },
                    complete: function () {
                      $grid.find(".item").attr("class", "item");
                      $popup
                        .find(".slick-active .image")
                        .velocity("stop")
                        .velocity({
                            opacity: 1,
                          },
                          600
                        );
                      $popup
                        .find(".slick-active figcaption")
                        .velocity("stop")
                        .velocity({
                          opacity: 1,
                          translateZ: 0, // Force HA by animating a 3D property
                          translateY: [0, "20px"],
                        }, {
                          duration: 500,
                          delay: 400,
                        });
                      $closeButton.on("click", function (e) {
                        closeShowcase();
                      });
                      $(document).on("click", function (e) {
                        if (
                          $(e.target).hasClass("slick-carousel") ||
                          e.target.tagName.toLowerCase() == "figure"
                        ) {
                          closeShowcase();
                        }
                      });
                    },
                  });
                }, 100 * $items.length);
              });

              $(document).on("keyup", function (e) {
                $items = $grid.masonry("getItemElements");
                if (e.keyCode == 27) {
                  closeShowcase();
                }
              });

              function closeShowcase() {
                $popup.velocity("stop").velocity({
                  opacity: 0,
                }, {
                  complete: function () {
                    $popup.css("z-index", -1);
                  },
                });
                $footer.velocity("stop").velocity({
                  opacity: 1,
                });
                $.each($items, function (index) {
                  var $this = $(this);
                  setTimeout(function () {
                    $this.children().attr("class", "").addClass("fadeInDown");
                  }, 100 * index);
                });
              }
            });
            $popup.append($slick);
          });
      }
    },
  };

  Drupal.behaviors.sideNav = {
    attach: function (context, settings) {
      $("body", context).once("sideNav", function () {
        var nav = false,
          level = 1,
          columnCurrent;

        $(document).on("click", function (event) {
          if ($(event.target).hasClass("sidebar-opacity")) {
            columnCurrent = 1;
            closeCols();
            $("#sidebar")
              .velocity("stop")
              .velocity({
                translateZ: 0, // Force HA by animating a 3D property
                translateX: "-100%",
              }, {
                delay: $(".column.active").length * 600,
                complete: function () {
                  $(this).hide();
                },
              });
            $(".sidebar-opacity")
              .velocity("stop")
              .velocity({
                opacity: 0,
              }, {
                delay: $(".column.active").length * 600,
                complete: function () {
                  $(this).hide();
                },
              });
            $("html, body").css("overflow-y", "auto");
            nav = false;
          }
        });

        $("#nav-toggle, #close-aside").on("click", function () {
          if (nav) {
            columnCurrent = 1;
            closeCols();
            $("#sidebar")
              .velocity("stop")
              .velocity({
                translateZ: 0, // Force HA by animating a 3D property
                translateX: "-100%",
              }, {
                display: "none",
                delay: 600 * (level - 1),
              });
            $(".sidebar-opacity")
              .velocity("stop")
              .velocity({
                opacity: 0,
              }, {
                display: "none",
                delay: 600 * (level - 1),
              });
            $("html, body").css("overflow-y", "auto");
            nav = false;
          } else {
            $("#sidebar")
              .velocity("stop")
              .velocity({
                translateZ: 0, // Force HA by animating a 3D property
                translateX: [0, "-100%"],
              }, {
                display: "block",
              });
            $(".sidebar-opacity").velocity("stop").velocity({
              opacity: 1,
            }, {
              display: "block",
            });
            $("html, body").css("overflow-y", "hidden");
            nav = true;
          }
        });

        $(".menu-close:not(#close-aside)").on("click", function () {
          closeCols();
          columnCurrent = columnCurrent - 1;
        });

        $("#sidebar").on("mouseover", function (event) {
          var column = parseInt(
            $(event.target).parents(".column").attr("data-column")
          );
          if ($(window).width() > 1200) {
            if ($(event.target).closest("li.expanded").length > 0) {
              var id = $(event.target)
                .closest("li.expanded")
                .attr("class")
                .split(" ")
                .pop();
              if (
                $('div[data-column="' + (column + 1) + '"]').attr("id") !== id
              ) {
                navBehavior($(event.target).closest("li.expanded"));
              } else {
                columnCurrent = column + 1;
                closeCols();
              }
            } else if (
              $(event.target).closest("li.leaf").length > 0 &&
              $('div[data-column="' + (column + 1) + '"]').length > 0
            ) {
              columnCurrent = column;
              closeCols();
            } else if (
              $(event.target).closest("li.leaf").length == 0 &&
              $(event.target).parents(".column").length > 0
            ) {
              columnCurrent = column;
              closeCols();
            }
          }
        });

        $("#sidebar").on("tap click", ".arrow", function (event) {
          navBehaviorMobile($(this).parent().parent());
          event.preventDefault();
        });

        function navBehavior($element) {
          columnCurrent = $element.parents(".column").data("column");
          var id = $element.attr("class").split(" ").pop();
          if (Math.abs(level - columnCurrent <= 1)) {
            var $menu = $element.children("ul.menu").clone(),
              title = $element.children("a").text(),
              $columnNext = $('div[data-column="' + (columnCurrent + 1) + '"]');

            if (!$columnNext.hasClass("active")) {
              $columnNext.find(".inner").empty().html($menu);
              $columnNext.velocity("stop").velocity({
                translateZ: 0,
                translateX: [0, "-100%"],
              }, {
                duration: 600,
                begin: function () {
                  $(this).show();
                  $columnNext.find(".scrollbar").simplebar("recalculate");
                },
                complete: function () {
                  $columnNext.attr("id", id);
                  $columnNext.addClass("active");
                },
              });
            } else {
              $columnNext
                .next(".active")
                .velocity("stop")
                .velocity({
                  translateZ: 0,
                  translateX: "-100%",
                }, {
                  display: "none",
                  duration: 0,
                  delay: 0,
                  complete: function () {
                    $(this).find(".inner").empty();
                    $(this).attr("id", "").removeClass("active");
                  },
                });
              $columnNext.find(".inner").empty().html($menu);
              $(this).show();
              $columnNext.find(".scrollbar").simplebar("recalculate");
              $columnNext.attr("id", id);
              $columnNext.addClass("active");
            }
          }
        }

        function navBehaviorMobile($element) {
          columnCurrent = $element.parents(".column").data("column");
          var id = $element.attr("class").split(" ").pop();
          if (Math.abs(level - columnCurrent <= 1)) {
            var $menu = $element.children("ul.menu").clone(),
              title = $element.children("a").text(),
              $columnNext = $('div[data-column="' + (columnCurrent + 1) + '"]');

            if (!$columnNext.hasClass("active")) {
              $columnNext.find(".inner").empty().html($menu);
              $columnNext.velocity("stop").velocity({
                translateZ: 0,
                translateX: [0, "-100%"],
              }, {
                duration: 600,
                begin: function () {
                  $(this).show();
                  $columnNext.find(".scrollbar").simplebar("recalculate");
                },
                complete: function () {
                  $columnNext.attr("id", id);
                  $columnNext.addClass("active");
                },
              });
            } else {
              closeCols();
              setTimeout(function () {
                $columnNext.velocity("stop").velocity({
                  translateZ: 0,
                  translateX: 0,
                }, {
                  duration: 600,
                  begin: function () {
                    $columnNext.find(".inner").empty().html($menu);
                    $(this).show();
                    $columnNext.find(".scrollbar").simplebar("recalculate");
                  },
                  complete: function () {
                    $columnNext.attr("id", id);
                    $columnNext.addClass("active");
                  },
                });
              }, $(".column.active").length * 600);
            }
          }
        }

        function closeCols() {
          $($(".column.active").get().reverse()).each(function (index) {
            if (parseInt($(this).attr("data-column")) > columnCurrent) {
              $(this)
                .velocity("stop")
                .velocity({
                  translateZ: 0,
                  translateX: "-100%",
                }, {
                  display: "none",
                  duration: 600,
                  delay: 600 * index,
                  complete: function () {
                    $(this).find(".inner").empty();
                    $(this).attr("id", "").removeClass("active");
                    level = index;
                  },
                });
            }
          });
        }
      });
    },
  };

